<?require_once('base_model.php');class StudentSignUp_Model extends Base_Model {    function __construct()    {        parent::__construct();    }          function save_studentsignup($details)    {        foreach ($details as &$value)        {            $value = '\'' . $value . '\'';        }                $this->db->query('INSERT INTO persons(lastname, firstname, middlename, namesuffix, sex, birthday)' .                         ' values(' . $details['lastname'] . ', ' . $details['firstname'] . ', ' . $details['middlename'] . ', ' .                         $details['namesuffix'] . ', ' . $details['sex'] . ', ' . $details['birthday'] .');');                $pid = $this->db->query('SELECT personid FROM persons where lastname =' . $details['lastname'] . 'and firstname = ' . $details['firstname'] .                                 ' and middlename =' . $details['middlename'] . ' and namesuffix = ' . $details['namesuffix'] . ' and sex = ' .                                 $details['sex'] . ' and birthday = ' . $details['birthday'] . ';');        $pid = $pid->result_array();        $pid = $pid[0]['personid'];                $this->db->query('INSERT INTO users(personid, login, password)' . ' values(' . $pid . ', ' . $details['username'] . ', ' . $details['password'] . ');');                if($details['mobilenumber'] != '\'\'')        {            $this->db->query('INSERT INTO contactdetails(contacttypeid, personid, contactinfo) values(1, ' . $pid . ', ' . $details['mobilenumber'] . ');');         }        $this->db->query('INSERT INTO contactdetails(contacttypeid, personid, contactinfo) values(2, ' . $pid . ', ' . $details['emailadd'] . ');');         $this->db->query('INSERT INTO students(personid) values(' . $pid . ');');                return;    }}