<div class="container">    <div class="row-fluid" style="padding-left:15px">        <div class="span12 well" align="center">			<div style="width:50%;" align="left">				<form action="<?= base_url('studentsignup/get_details')?>" method="post" enctype="multipart/form-data"> 					<h4> Site Information </h4>					<label> Username: </label>					<input type="text" name="username" class="span6"/>					<label> Password: </label>					<input type="password" name="password" class="span6"/>										<h4> Basic Information </h4>					<label> Name: </label>					<table>						<tr>							<td style="padding-right:25px"> <input type="text" placeholder="Last Name" name="lastname"/> </td>							<td style="padding-left:25px"> <input type="text" placeholder="First Name" name="firstname"/> </td>						</tr>						<tr>							<td style="padding-right:25px"> <input type="text" placeholder="Middle Name" name="middlename"/> </td>							<td style="padding-left:25px"> <input type="text" placeholder="Name Suffix" name="namesuffix"/> </td>						</tr>					</table>					<label> Sex: </label>					<input type="radio", name="sex", value="0"> Male </input>					<input type="radio", name="sex", value="1"> Female </input>					<label> Birthday: (mm/dd/yyyy) </label> 					<input type="text" name="birthday" />					<h4> Contact Information </h4>					<label> Mobile Number </label>					<input type="text" name="mobilenumber" class="span6"/>					<label> Email Address</label>					<input type="text" name="emailadd" class="span6"/>					<br/>					<br/>					<center>						<input type="submit" value="Sign up" class="btn btn-custom btn-large">					</center>				</form>			</div>        </div>    </div></div>